// Consolidate all scraped data into structured format
const sources = $input.all();
const consolidatedData = {
  council: [],
  planning: [],
  education: [],
  public_safety: [],
  public_works: [],
  collection_date: new Date().toISOString(),
  source_count: sources.length
};

sources.forEach(source => {
  const data = source.json;
  const sourceType = detectSourceType(source.node?.name || '');

  if (sourceType && consolidatedData[sourceType]) {
    consolidatedData[sourceType].push({
      raw_data: data,
      source_url: source.url || 'unknown',
      processed_at: new Date().toISOString(),
      needs_ai_processing: true
    });
  }
});

function detectSourceType(nodeName) {
  const name = nodeName.toLowerCase();
  if (name.includes('council')) return 'council';
  if (name.includes('planning') || name.includes('zoning')) return 'planning';
  if (name.includes('education') || name.includes('hcpss')) return 'education';
  if (name.includes('safety') || name.includes('police')) return 'public_safety';
  if (name.includes('works') || name.includes('road')) return 'public_works';
  return 'council'; // Default fallback
}

// n8n requires returning an array of items
return [consolidatedData];

// Process raw government data into structured content for newsletter
const rawData = $json;

// Simulate AI processing with rule-based content extraction
const processedContent = {
  council: processCouncilData(rawData.council || []),
  planning: processPlanningData(rawData.planning || []),
  education: processEducationData(rawData.education || []),
  public_safety: processPublicSafetyData(rawData.public_safety || []),
  public_works: processPublicWorksData(rawData.public_works || []),
  processing_timestamp: new Date().toISOString(),
  total_items_processed: calculateTotalItems(rawData)
};

function processCouncilData(councilItems) {
  return councilItems.map(item => ({
    ...item,
    ai_analysis: {
      title: extractTitle(item.raw_data) || 'County Council Update',
      summary: extractSummary(item.raw_data) || 'County council meeting information',
      urgency: determineUrgency(item.raw_data),
      neighborhoods: extractNeighborhoods(item.raw_data),
      topics: extractTopics(item.raw_data, 'council'),
      date: item.processed_at,
      sentiment: analyzeSentiment(item.raw_data)
    }
  }));
}

function processPlanningData(planningItems) {
  return planningItems.map(item => ({
    ...item,
    ai_analysis: {
      title: extractTitle(item.raw_data) || 'Planning & Zoning Update',
      summary: extractSummary(item.raw_data) || 'Planning and zoning activity update',
      urgency: 'normal',
      neighborhoods: extractNeighborhoods(item.raw_data),
      topics: ['development', 'zoning', 'planning'],
      date: item.processed_at,
      sentiment: { score: 0, label: 'neutral' }
    }
  }));
}

function processEducationData(educationItems) {
  return educationItems.map(item => ({
    ...item,
    ai_analysis: {
      title: extractTitle(item.raw_data) || 'HCPSS Education Update',
      summary: extractSummary(item.raw_data) || 'Howard County Public Schools update',
