      neighborhoods: ['county_wide'],
      topics: ['education', 'schools', 'students'],
      date: item.processed_at,
      sentiment: analyzeSentiment(item.raw_data)
    }
  }));
}

function processPublicSafetyData(safetyItems) {
  return safetyItems.map(item => ({
    ...item,
    ai_analysis: {
      title: extractTitle(item.raw_data) || 'Public Safety Update',
      summary: extractSummary(item.raw_data) || 'Public safety and emergency information',
      urgency: 'high', // Safety items generally higher priority
      neighborhoods: extractNeighborhoods(item.raw_data),
      topics: ['public_safety', 'police', 'fire', 'emergency'],
      date: item.processed_at,
      sentiment: { score: 0, label: 'neutral' }
    }
  }));
}

function processPublicWorksData(worksItems) {
  return worksItems.map(item => ({
    ...item,
    ai_analysis: {
      title: extractTitle(item.raw_data) || 'Public Works Update',
      summary: extractSummary(item.raw_data) || 'Infrastructure and public works update',
      urgency: 'normal',
      neighborhoods: extractNeighborhoods(item.raw_data),
      topics: ['transportation', 'infrastructure', 'roads'],
      date: item.processed_at,
      sentiment: { score: 0, label: 'neutral' }
    }
  }));
}

function extractTitle(data) {
  if (typeof data === 'string') {
    // Extract first line or first sentence as title
    const lines = data.split('\n').filter(line => line.trim().length > 0);
    return lines[0] ? lines[0].substring(0, 100).trim() : null;
  }
  return data?.title || data?.name || null;
}

function extractSummary(data) {
  if (typeof data === 'string') {
    // Extract first paragraph or first 200 chars as summary
    const paragraphs = data.split('\n\n');
    const firstParagraph = paragraphs[0] || data;
    return firstParagraph.substring(0, 200).trim() + (firstParagraph.length > 200 ? '...' : '');
  }
  return data?.description || data?.summary || null;
}

function determineUrgency(data) {
  const urgentKeywords = ['emergency', 'urgent', 'alert', 'breaking', 'immediate', 'critical'];
  const highKeywords = ['important', 'deadline', 'hearing', 'vote', 'decision'];

  const text = JSON.stringify(data).toLowerCase();

  if (urgentKeywords.some(keyword => text.includes(keyword))) {
    return 'urgent';
  } else if (highKeywords.some(keyword => text.includes(keyword))) {
    return 'high';
  }
  return 'normal';
}

function extractNeighborhoods(data) {
  const neighborhoods = ['columbia', 'ellicott_city', 'elkridge', 'clarksville', 'highland', 'savage', 'laurel', 'fulton'];
  const text = JSON.stringify(data).toLowerCase();

  const found = neighborhoods.filter(neighborhood => 
    text.includes(neighborhood.replace('_', ' ')) || text.includes(neighborhood)
  );

  return found.length > 0 ? found : ['county_wide'];
}

function extractTopics(data, defaultCategory) {
  const topicMap = {
    council: ['government', 'policy', 'budget'],
    planning: ['development', 'zoning', 'land_use'],
    education: ['schools', 'students', 'curriculum'],
    public_safety: ['police', 'fire', 'emergency'],
    public_works: ['roads', 'infrastructure', 'utilities']
  };

  return topicMap[defaultCategory] || ['general'];
}

function analyzeSentiment(data) {
  // Simple keyword-based sentiment analysis
  const text = JSON.stringify(data).toLowerCase();

  const positiveWords = ['approve', 'support', 'benefit', 'improve', 'success', 'good', 'excellent'];
  const negativeWords = ['oppose', 'concern', 'problem', 'issue', 'failure', 'bad', 'poor'];

  const positiveCount = positiveWords.filter(word => text.includes(word)).length;
  const negativeCount = negativeWords.filter(word => text.includes(word)).length;

  if (positiveCount > negativeCount) {
    return { score: 0.3, label: 'positive' };
  } else if (negativeCount > positiveCount) {
    return { score: -0.3, label: 'negative' };
  }
  return { score: 0, label: 'neutral' };
}

function calculateTotalItems(data) {
  return Object.values(data).reduce((total, items) => {
    return total + (Array.isArray(items) ? items.length : 0);
  }, 0);
}

return [processedContent];

