const processedData = $json;
const stories = [];

// Process each category and extract key stories
Object.entries(processedData).forEach(([category, items]) => {
  if (Array.isArray(items)) {
    items.forEach(item => {
      if (item.ai_analysis) {
        const story = {
          category: category,
          title: item.ai_analysis.title,
          summary: item.ai_analysis.summary,
          impact_score: calculateImpactScore(item.ai_analysis),
          affected_areas: item.ai_analysis.neighborhoods || ['county_wide'],
          topics: item.ai_analysis.topics || [],
          urgency: item.ai_analysis.urgency || 'normal',
          source_date: item.ai_analysis.date,
          full_content: item.raw_data
        };
        stories.push(story);
      }
    });
  }
});

// Sort by impact score and select top stories
const topStories = stories
  .sort((a, b) => b.impact_score - a.impact_score)
  .slice(0, 15);

function calculateImpactScore(analysis) {
  let score = 0;

  // Base score from urgency
  if (analysis.urgency === 'urgent') score += 50;
  else if (analysis.urgency === 'high') score += 30;
  else if (analysis.urgency === 'normal') score += 10;

  // Add points for broader impact
  if (analysis.neighborhoods && analysis.neighborhoods.includes('county_wide')) {
    score += 20;
  } else if (analysis.neighborhoods && analysis.neighborhoods.length > 2) {
    score += 15;
  }

  // Add points for important topics
  const importantTopics = ['budget', 'education', 'development', 'public_safety'];
  const topicMatch = (analysis.topics || []).filter(topic => 
    importantTopics.includes(topic)
  ).length;
  score += topicMatch * 10;

  return score;
}

return [{
  top_stories: topStories,
  total_stories: stories.length,
  categories_processed: Object.keys(processedData).length,
  processing_date: new Date().toISOString()
}];

