function generateIssueNumber() {
  const startOfYear = new Date(new Date().getFullYear(), 0, 1);
  const weekNumber = Math.ceil((new Date() - startOfYear) / (7 * 24 * 60 * 60 * 1000));
  return `${new Date().getFullYear()}-${weekNumber.toString().padStart(2, '0')}`;
}

function estimateReadTime(text) {
  const wordsPerMinute = 200;
  const wordCount = text.split(' ').length;
  return Math.max(1, Math.ceil(wordCount / wordsPerMinute)) + ' min read';
}

function formatNeighborhoodName(name) {
  return name.split('_')
    .map(word => word.charAt(0).toUpperCase() + word.slice(1))
    .join(' ');
}

function extractUpcomingItems(stories) {
  return stories
    .filter(story => story.summary.includes('upcoming') || story.summary.includes('scheduled'))
    .slice(0, 5)
    .map(story => ({
      title: story.title,
      type: story.category,
      timeline: 'Next 30 days'
    }));
}

function getNextCouncilMeeting() {
  // This would typically pull from a calendar API
  const nextMonday = new Date();
  nextMonday.setDate(nextMonday.getDate() + (1 + 7 - nextMonday.getDay()) % 7);
  return nextMonday.toLocaleDateString();
}

function getCommentDeadlines() {
  const deadline = new Date();
  deadline.setDate(deadline.getDate() + 5);
  return deadline.toLocaleDateString();
}

function extractMetric(stories, type) {
  // Extract numerical data from stories
  const relevantStories = stories.filter(story => 
    story.summary.toLowerCase().includes(type.replace('_', ' '))
  );

  if (relevantStories.length === 0) return 'N/A';

  // Simple extraction - in practice, this would use more sophisticated parsing
  const numbers = relevantStories[0].summary.match(/\d+/g);
  return numbers ? numbers[0] : 'Multiple';
}

