function calculateRelevanceScore(story, subscriber) {
  let score = 50; // Base score

  // Boost for neighborhood match
  if (story.affected_areas && subscriber.neighborhood) {
    const hasNeighborhoodMatch = story.affected_areas.some(area => 
      area.toLowerCase().includes(subscriber.neighborhood.toLowerCase())
    );
    if (hasNeighborhoodMatch) score += 30;
  }

  // Boost for topic match
  if (story.topics && subscriber.topics) {
    const topicMatches = story.topics.filter(topic =>
      subscriber.topics.includes(topic)
    ).length;
    score += topicMatches * 15;
  }

  // Boost for urgency
  if (story.urgency === 'urgent') score += 25;
  else if (story.urgency === 'high') score += 15;

  return Math.min(100, score);
}

